#include <stdio.h>int main() {    int num1, num2, num3, num4, opt;    printf("Enter the first fraction :");    scanf_s("%d/%d", &num1, &num2);    printf("Enter the second fraction :");    scanf_s("%d/%d", &num3, &num4);    printf("\nInput your option :\n");    printf("1:Addition.\n2:Substraction.\n3:Multiplication.\n4:Division.\n5:Exit.\n");    scanf_s("%d", &opt);    switch (opt) {    case 1:        printf("The Addition of  %d/%d and %d/%d is: %d\n", num1, num2, num3, num4, num1 / num2 + num3 / num4); break;    case 2:        printf("The Subtraction of %d/%d  and %d/%d is: %d\n", num1, num2, num3, num4, num1 / num2 - num3 / num4); break;    case 3:        printf("The Multiplication of %d/%d  and %d/%d is: %d\n", num1, num2, num3, num4, num1 / num2 * num3 / num4); break;    case 4:        if (num2 == 0)            printf("The second integer is zero. Divide by zero.\n");        else            printf("The Division of %d/%d  and %d/%d is : %d\n", num1, num2, num3, num4, (num1 / num2) / (num3 / num4));        break;    case 5: break;    default:        printf("Input correct option\n");        break;    }}